-- DEMO QUERIES
SELECT store_code, SUM(total) AS revenue FROM sales GROUP BY store_code;
SELECT p.name, COUNT(*) AS sold_count
FROM sale_items si JOIN products p ON si.product_id = p.product_id
GROUP BY p.name ORDER BY sold_count DESC LIMIT 5;
SELECT member, SUM(total) FROM sales GROUP BY member;
SELECT promotion, COUNT(*) AS txn_count, SUM(total) AS revenue FROM sales GROUP BY promotion;
